
public class EjemploUno {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Hola mundo!!!");
		//Éste es un comentario de una línea. Esta información no se procesa
		
		System.out.println("El valor de x es: " + "5");
		//La línea anterior, contiene una concatenación de Strings
		
		System.out.println("El valor de x es: " + (3+3));
		//Concatenación de un String + el resultado de una operación matemática
		System.out.println(""); // Salto de línea
		
		int num = 5;
		//Variable numérica de tipo Entero
		
		System.out.println("5x1=" + (num*1));
		System.out.println("5x2=" + (num*2));
		System.out.println("5x3=" + (num*3));
		System.out.println("5x4=" + (num*4));
		System.out.println("5x5=" + (num*5));
	}

}
